components/common/paginator:
{{#unless links.length '==' 0}}
    <div class="pagination {{#if top_pagination}}top-pagination{{/if}}">
        <ul class="pagination-list">
            {{#if previous}}
                <li class="pagination-item pagination-item--previous">
                    <a class="pagination-link" href="{{previous}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>
                        <i class="icon icon--play" aria-hidden="true">
                            <svg>
                                <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-caret-left"></use>
                            </svg>
                        </i>
                    </a>
                </li>
            {{/if}}
            {{#each links}}
                {{#if this.number '==' ../current}}
                    <li class="pagination-item pagination-item--current">
                        <a class="pagination-link" href="{{this.url}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>{{this.number}}</a>
                    </li>
                {{/if}}
                {{#if @last}}
                    <li class="pagination-item pagination-item--last">
                        <a class="pagination-link count {{../../products_on_page}}-{{../../product_count}}" href="{{this.url}}" {{#unless reload}}data-faceted-search-facet{{/unless}}
                        data-page-number="
                        {{#if ../../products_on_page '===' ../../product_count}}
                            {{../../../page_total}}
                        {{else}}
                            {{../../../current}}
                        {{/if}}">
                        {{#if ../../products_on_page '===' ../../product_count}}
                            / {{../../../page_total}}
                        {{else}}
                            / {{../../../current}}
                        {{/if}}
                        </a>
                    </li>
                {{/if}}
            {{/each}}

            {{#if next}}
                <li class="pagination-item pagination-item--next">
                    <a class="pagination-link" href="{{next}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>
                        <i class="icon icon--play" aria-hidden="true">
                            <svg>
                                <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-caret-right"></use>
                            </svg>
                        </i>
                    </a>
                </li>
            {{/if}}
        </ul>
    </div>
{{/unless}}

components/category/shop-by-price:
{{#if theme_settings.shop_by_price_visibility}}
    <div class="sidebarBlock">
        <h5 class="sidebarBlock-heading">{{lang 'category.shop_by_price'}}</h5>
        <ul class="navList">
            {{#each shop_by_price}}
            <li class="navList-item">
                <a class="navList-action {{#if selected }} is-active {{/if}}" href="{{url}}" alt="{{low.formatted}} - {{high.formatted}}" title="{{low.formatted}} - {{high.formatted}}">{{low.formatted}} - {{high.formatted}}</a>
            </li>
            {{/each}}

            {{#any shop_by_price selected=true}}
                <li class="navList-item">
                    <a href="{{category_url}}" class="navList-action">
                        {{lang 'category.reset'}}
                    </a>
                </li>
            {{/any}}
        </ul>
    </div>
{{/if}}

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    <div
        class="accordion-navigation toggleLink {{#unless ../start_collapsed }} is-open {{/unless}}"
        role="button"
        data-collapsible="#facetedSearch-content--{{hyphenate facet }}">
        <h5 class="accordion-title">
           FILTER BY {{ title }}
        </h5>

        <div class="accordion-navigation-actions">
            <svg class="icon accordion-indicator toggleLink-text toggleLink-text--off">
                <use xlink:href="#icon-add" />
            </svg>
            <svg class="icon accordion-indicator toggleLink-text toggleLink-text--on">
                <use xlink:href="#icon-remove" />
            </svg>
        </div>
        {{#if selected}}
            <a href="{{ remove_url }}" class="facetedSearch-clearLink" data-faceted-search-facet>{{lang 'search.faceted.clear'}}</a>
        {{/if}}
    </div>

    <div id="facetedSearch-content--{{hyphenate facet }}" class="accordion-content {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul id="facetedSearch-navList--{{hyphenate facet }}" class="navList" data-facet="{{facet}}" data-has-more-results="{{ has_more_results }}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{ url }}"
                        class="navList-action navList-action--checkbox {{#if selected }} is-selected {{/if}} {{#if has_sub_categories }} has-children {{/if}}"
                        rel="nofollow"
                        data-id="{{ id }}"
                        data-faceted-search-facet>
                        {{ title }}
                        {{#if ../show_product_counts}}
                            {{#if count}}
                                <span>({{ count }})</span>
                            {{/if}}
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{hyphenate facet }}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}
