components/category/product-listing:
{{> components/products/filter sort=pagination.category.sort}}

<div class="product-grid__container">
    <form action="{{urls.compare}}" method='POST' data-product-compare class="product-grid-container">
        {{#if theme_settings.product_list_display_mode '===' 'grid'}}
        {{> components/products/grid products=category.products show_compare=category.show_compare
        theme_settings=theme_settings}}
        {{else}}
        {{> components/products/list products=category.products show_compare=category.show_compare
        theme_settings=theme_settings}}
        {{/if}}

    </form>
</div>

<div class="pagination-bottom">
        {{> components/common/paginator
            pagination.category
            product_count=category.count
            page_total=(ceil (divide category.total_products category.count))
            products_on_page=theme_settings.categorypage_products_per_page
        }}
</div>

components/common/paginator:
{{#unless links.length '==' 0}}
    <div class="pagination {{#if top_pagination}}top-pagination{{/if}}">
        <ul class="pagination-list">
            {{#if previous}}
                <li class="pagination-item pagination-item--previous">
                    <a class="pagination-link" href="{{previous}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>
                        <i class="icon icon--play" aria-hidden="true">
                            <svg>
                                <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-caret-left"></use>
                            </svg>
                        </i>
                    </a>
                </li>
            {{/if}}
            {{#each links}}
                {{#if this.number '==' ../current}}
                    <li class="pagination-item pagination-item--current">
                        <a class="pagination-link" href="{{this.url}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>{{this.number}}</a>
                    </li>
                {{/if}}
                {{#if @last}}
                    <li class="pagination-item pagination-item--last">
                        <a class="pagination-link count {{../../products_on_page}}-{{../../product_count}}" href="{{this.url}}" {{#unless reload}}data-faceted-search-facet{{/unless}}
                        data-page-number="
                        {{#if ../../products_on_page '===' ../../product_count}}
                            {{../../../page_total}}
                        {{else}}
                            {{../../../current}}
                        {{/if}}">
                        {{#if ../../products_on_page '===' ../../product_count}}
                            / {{../../../page_total}}
                        {{else}}
                            / {{../../../current}}
                        {{/if}}
                        </a>
                    </li>
                {{/if}}
            {{/each}}

            {{#if next}}
                <li class="pagination-item pagination-item--next">
                    <a class="pagination-link" href="{{next}}" {{#unless reload}}data-faceted-search-facet{{/unless}}>
                        <i class="icon icon--play" aria-hidden="true">
                            <svg>
                                <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-caret-right"></use>
                            </svg>
                        </i>
                    </a>
                </li>
            {{/if}}
        </ul>
    </div>
{{/unless}}

